        }catch (e) {
        }
    }
    // 2.æ‹¿åˆ°å•ä¸ªä»“åº“çš„è§„åˆ™åˆ—è¡¨
    if (mRemoteRules.length != 0) {
        // æ¢ä¸ªé”¤å­çš„äºŒåˆ†æ³•ï¼Œä¸€å®šè¦éå†æ‰€æœ‰æ•°æ®çš„ï¼Œè¿™æ ·é¡¶å¤šæ˜¯O(1/2 n)ï¼Œå¿½ç•¥å¸¸æ•°è¿˜æ˜¯O(n)ï¼Œç¡®å®ä¼šå¿«ä¸€ä¸¢ä¸¢ï¼Œç©ºé—´æ¢æ—¶é—´çš„ç»“æœ
        var left = 0;
