    pic_url: o.cover.includes('default') && defaultPic ? defaultPic : o.cover,
    url: 'https://movie.douban.com/subject/'+o.id+'/?from=showing'
  };
});

setHomeResult({ data: data });

} catch (e) {
    let result = {};
    let errorTitle = '““””<font color="grey">' + '暂无内容，请稍候再试' + '</font>';
    let errorDesc = '';
    try {
         result = JSON.parse(getResCode());
    } catch (e) { }
    if (result.msg != null && result.msg !== '') {
         errorTitle = '““' + result.msg + '””';
         errorDesc = '提示：也许只是服务器打了个盹等等就会好起来了？\n提示：也许你可以试试搜索？有惊喜哦～';
    }

    setHomeResult({
          data: [{
              title: errorTitle,
              desc: errorDesc,
              url: testMode === 1 ? 'https://m.douban.com/subject/26357307/' : '',
              col_type: 'text_center_1'
          }]
    });
}

