// 是否开启规则设置模式，开启/1 不开启/0
let settingsMode = 0;

let settings = {
    /**
     * 是否开启视界搜，开启后点击第一项即可使用
     *
     * 若不开启则点击为查看原文
     *
     * 开启/1 不开启/0
     */
    openHikerSearch: 1,
    // desc 描述的三行中每行的最大长度
    descLineMaxLen: 25,
    // 评论用户颜色
    userColor: "#2979FF",
    // 模块的间距
    line_model: 1,
    // 模块间距类型（适配以后可能会有的无边距线）
    line_type_model: 'line_blank',

    // 评分显示类型，0 为星星★☆，1 为文字
    ratingType: 0,
    // 是否显示'[查看原文]', 0 为不显示，1 为显示
    showSource: 0,
