                col_type: 'icon_small_3',
                url: mUrl
            });*/
            d.push({
                col_type: 'line',
            });
        } else {
            if (showInputTokenTips) {
                let pluginLatest = '海阔视界规则分享，当前分享的是：网页插件，Reborn￥js_url￥haikuoshijie.cn_获取用户Token@base64:';
                let pluginUrl = 'hiker://files/rules/js/haikuoshijie.cn_获取用户Token.js';
                let plugin = '';
                try {
                    plugin = fetch(pluginUrl, {});
                    if (plugin == null) plugin = '';
                } catch (e) {
                    plugin ='';
                }
                let pluginUpdate = 0;
                if (plugin !== '') {
                    if (pluginLatest.split('base64:\/\/')[1] !== base64Encode(plugin).replace(/\n/g, '')) pluginUpdate = 1;
                }
                d.push({
                    title: "<p></p>" + '<small><font color="grey">' +  '如需收藏或点赞功能，需登录论坛后执行以下步骤获取 Token：<br>[本提示可编辑规则关闭]<br>1. ' + ((plugin === '' || pluginUpdate === 1) ? '点击下方口令代码' + (pluginUpdate === 1 ? '<b>更新插件</b>' : '导入专用插件') + '，然后' : '你已导入最新专用插件，只需') + '<a href="' + MY_URL + '">[查看原文]</a>一次再返回刷新' + ((plugin === '' || pluginUpdate === 1) ? '<br><pre><code>' + pluginLatest + '</code></pre>' : '') + "</font></small>",
                    col_type: colType
                });
            }
        }

        if (showComment) {
            /*if (hasRule) {
            for(var i = 0; i < 2; i++)
            d.push({
                   col_type: 'line'
              });
            }*/

            /cursor:"(.*?)"/.test(html);
            let cursorTemp = RegExp.$1;
            let cursor = (cursorTemp != null && cursorTemp !== '') ? cursorTemp : '0';
            putVar({key: 'cursor', value: cursor});
            putVar({key: 'first_page_cursor', value: cursor});

            d.push({
                title: "<p></p>" + "<big><b>" + parseDomForHtml(html, '.comment-title&&Html') + "</b></big>",
                col_type: colType
            });
            var pl = parseDom(html, '.comments&&Html')
                .match(/<li[\s\S]*?<\/li/g);
            if (pl == null || pl.length === 0) {
                d.push({
                    title: "<p></p>" + '<font color="grey">暂无评论</font>',
                    col_type: colType
                });
            } else {
