                        .replaceWithCallback(/<img (src|data-src)="(.*?)"([\s\S]*?)>/g, "<img data-v>", imgTagReplacement2)
                        .replace(/<img([\s\S]*?)>/g, imgError)
                        .replace(/<showimg/g, '<img');
                    try {
                        let commentReplyText = parseDomForHtml(pl[j], '.comment-quote&&Html');
                        let commentQuoteUsername = parseDomForHtml(commentReplyText, ".quote-nickname&&Html");
                        let commentQuoteTime = parseDomForHtml(commentReplyText, ".quote-time&&Html");
