                            .replace(/<a[\s\S].*?<\/a>/g, '');
                        let commentReplyTextArray = commentReplyText.match(/<p[\s\S].*?<\/p>/g);
                        commentContent = abbreviateReplyText(commentContent, commentReplyTextArray)
                            .replace(commentQuoteUsername, '<font color="' + quoteUserColor + '">' + commentQuoteUsername + '</font>')
                            .replace(commentQuoteTime, '<font color="grey">' + commentQuoteTime + '</font>');
