function getBigText(text) {
    return '<big>' + text + '</big>';
}
function getSmallText(text) {
    return '<small>' + text + '</small>';
}
function getStrongText(text) {
    return '<strong>' + text + '</strong>';
}
function getTitleText(text) {
    return getStrongText(text);
}
function getColorText(text, color) {
    return '<font color="' + color + '">' + text + '</font>';
}
function getGreyText(text) {
    return getColorText(text, 'grey');
}
function getAuthorText(text) {
    return getColorText(text, userColor);
}
function getCodeText(text) {
    return '<code>' + text + '</code>';
}
function getBlockquoteText(text) {
    return '<blockquote>' + text + '</blockquote>'
}
function getAddressTag(url, text) {
    return '<a href="' + url + '">' + text + '</a>'
}
function getCommentMeta(html) {
    return getSmallText(html);
}
String.prototype.replaceWithCallback = function (searchValue, replaceValue, callback) {
    let returnValue = this;
    if (callback != null) {
        returnValue = callback(this, searchValue, replaceValue);
    }
    return returnValue;
};

let imgError = '<font color="grey">图片暂时无法显示，若要查看请<a href="' + MY_URL + '">访问原文</a></font>';
function imgTag(context, sourceTag, imgUrl) {
    if(/(w\/(\d+)\/interlace)/.test(imgUrl) === true) {
        sourceTag = sourceTag.replace(RegExp.$1, 'w/' + imgWidth + '/interlace');
    }
    let replaceString = sourceTag;
    let replaceString1 = imgUrl === ''
        ? imgError
        : ('<a href="' + imgUrl + '">'
            + (hideImg === 1 ? '[点击查看大图]' : '图片无法加载？点我试试~')
            + '</a>');
    if(showFullImgTips) {
        replaceString += ('<br>' + getSmallText(replaceString1));
    }
    if (hideImg) {
        replaceString = replaceString1;
    }
    return context.replace(/<img data-v>/g, replaceString);
}
function imgTagReplacement2(context, searchValue, replaceValue) {
    let matchArray = context.match(searchValue);
    let returnValue = context;
    if (matchArray != null) {
        for (let i = 0; i < matchArray.length; i++) {
            let item = matchArray[i];
            /(<img (src|data-src)="(.*?)"([\s\S]*?)>)/.test(item);
            let imgUrl = RegExp.$3;
            if (imgUrl !== '') {
                returnValue = returnValue.replace(item, replaceValue);
                returnValue = imgTag(returnValue, RegExp.$1,imgUrl);
            }
        }
    }
    return returnValue;
}

