/*
 *Nirvana
 *Kaitian Axe
 */
/* method start */
function fetchSringToObject(url, headers) {
    let file = fetch(url, headers || {});
    if (file == 'null' || file == null || file == 'undefined' || file == undefined || file == '') file = [];
    else file = JSON.parse(file);
    return file;
}
/* method end */

/* global start */
let domain = getVar("ktf_domain");
let fyall_url = MY_URL.match(/\$fy_all\{([^\}]+)\}/)[1];
/* global end */

/* history start */
function history() {
    let newhistory = this.fetchSringToObject("hiker://files/nirvana/nirvana_new_history");
    let historyArr = [];
    for (let key in newhistory) {
        newhistory[key].forEach((e) => {
            e.key = key;
        });
        historyArr = historyArr.concat(newhistory[key]);
    }

    let historyItems = [];
    let noHistory = true;
    if (historyArr.length <= 0) {
        noHistory = true;
        historyItems = [{
            title: "‘‘好像还没有历史播放记录呢，看个视频再回来看看。’’",
            col_type: "text_1",
            desc: "V10及之后的版本请先到记录管理合成新历史记录文件，点击进入记录管理",
            url: domain + "/#/history/",
        }]
    } else {
        noHistory = false;
        historyArr = historyArr.sort((a, b) => b.time - a.time);
        historyItems = historyArr.map(data => ({
            title: (data.isTop ? "📌 " : "") + data.title.replace(/[\n\r\t]/g, "") + "\n\n来源：" + (data.source || "未知来源"),
            url: data.isPlayUrl ? data.url + "/#/mp4" : domain + "/#/" + data.key + "/" + (data.type ? data.type + "/" : "") + data.id + "/" + base64Encode(JSON.stringify({
                url: data.url,
                text: data.text
            })).replace("/", "%2f"),
            pic_url: data.img ? "http://" + data.img.split("//")[1] + "@Referer=" + "http://" + data.img.split("//")[1] : "*",
            desc: "点击恢复到播放\t" + data.text.replace(/[\n\r\t]/g, "")
        }));
    }
    let hikerHome = fetchSringToObject("hiker://home");
    let headerArr = [{
            title: "小虾",
            url: "hiker://home@小虾",
            col_type: "icon_round_small_4",
            img: "https://cdn.jsdelivr.net/gh/Lingyan000/pic@master/img/20201008222241.png",
            isUse: false
        },
        {
            title: "小橘",
            url: "hiker://home@小橘",
            col_type: "icon_round_small_4",
            img: "https://cdn.jsdelivr.net/gh/Lingyan000/pic@master/img/20201008222327.png",
            isUse: false
        },
        {
            title: "卡布奇洛",
            url: "hiker://home@卡布奇洛",
            col_type: "icon_round_small_4",
            img: "https://cdn.jsdelivr.net/gh/Lingyan000/pic@master/img/20201008222457.png",
            isUse: false
        },
        {
            title: "迪迦",
            url: "hiker://home@迪迦",
            col_type: "icon_round_small_4",
            img: "https://cdn.jsdelivr.net/gh/Lingyan000/pic@master/img/20201024204247.png",
            isUse: false
        },
        {
            title: "玄奘",
            url: "hiker://home@玄奘",
            col_type: "icon_round_small_4",
            img: "https://cdn.jsdelivr.net/gh/Lingyan000/pic@master/img/20201024203741.png",
            isUse: false
        }
    ];
    headerArr.forEach(header => {
        hikerHome.forEach(home => {
            if (header.title == home.title)
                header.isUse = true;
        });
    });
    headerArr = headerArr.filter(header => header.isUse);
    let historyGroup = [{
            col_type: "line_blank",
        },
        {
            title: `‘‘’’<strong>❀ 播放记录 ❀</strong>`,
            col_type: "text_center_1",
            url: "toast://好家伙,啥都要点上一点"
        }
    ];
    if (!noHistory) historyGroup = historyGroup.concat([{
            title: "管理播放记录",
            url: domain + "/#/history/",
            col_type: "icon_2",
            img: "https://cdn.jsdelivr.net/gh/Lingyan000/pic@master/img/20201008221635.png"
        },
        {
            title: "清空播放记录",
            url: `hiker://empty@lazyRule=.js:writeFile("hiker://files/nirvana/nirvana_new_history","");"toast://清空成功,刷新就冇了"`,
            col_type: "icon_2",
            img: "https://cdn.jsdelivr.net/gh/Lingyan000/pic@master/img/20201008221550.png"
        }
    ]);
    setHomeResult({
        data: headerArr.concat(historyGroup).concat(historyItems)
    });
}
/* history end */

/* update start */


function update() {
    function getUpdateStatus(name, version) {
        let hikerHome = fetchSringToObject("hiker://home");
        let status = {
            title: "",
            sort: "",
            localVersion: ""
        };
        let isHaveRule = hikerHome.some((data) => {
            if (data.title == name) {
                if (version > data.version) {
                    status.title = `<strong><font color="#ff6464">有更新</font></strong>`;
                    status.sort = 3;
                    status.localVersion = data.version;
                } else if (version <= data.version) {
                    status.title = `<strong><font color="#000">没有更新</font></strong>`;
                    status.sort = 1;
                    status.localVersion = data.version;
                }
            }
            return data.title == name;
        });
        if (!isHaveRule) {
            status.title = `<strong><font color="#ff8264">还没有导入规则</font></strong>`;
            status.sort = 2;
            status.localVersion = false;
        }
        return status;
    }
    let res = [];
    try {
        res = fetchSringToObject("https://hiker-box.pyt.ink/nirvana/nirvanaUpdate.json");
        if (res == 'null' || res == null || res == 'undefined' || res == undefined || res == '')
            res = fetchSringToObject("https://raw.fastgit.org/Lingyan000/hiker-box/main/nirvana/nirvanaUpdate.json");
        if (res == 'null' || res == null || res == 'undefined' || res == undefined || res == '')
            res = fetchSringToObject("https://gh-proxy.henryjiu.workers.dev/https://raw.githubusercontent.com/Lingyan000/hiker-box/main/nirvana/nirvanaUpdate.json");
        if (res == 'null' || res == null || res == 'undefined' || res == undefined || res == '')
            res = fetchSringToObject("https://raw.githubusercontent.com/Lingyan000/hiker-box/main/nirvana/nirvanaUpdate.json");
    } catch (err) {
        res = fetchSringToObject("https://gitee.com/qingbailingyan/impurity/raw/master/nirvanaUpdate.json");
    }
    let updateAllData = res;
    let hikerHome = fetchSringToObject("hiker://home");
    let items = [];
    let pastIdArr = [];
    updateAllData.forEach((updata, index) => {
        pastIdArr.push(updata.clipboardID)
        let status = getUpdateStatus(updata.name, updata.version);
        items = items.concat([{
                col_type: "line_blank",
                sort: status.sort
            }, {
                title: `““””「` + updata.name + `」` + status.title,
                col_type: "text_1",
                desc: (status.localVersion ? ("远程版本:V" + updata.version + "\t 本地版本:V" + status.localVersion) : "") + "\n" + (updata.isLogin ? "建议注册用户使用" : "") + "\n点击直接导入",
                url: `hiker://empty@lazyRule=.js:let fetchSringToObject=(url,headers)=>{let file=fetch(url,headers||{});if(file=='null'||file==null||file=='undefined'||file==undefined||file=='')file=[];else file=JSON.parse(file);return file}let ruleObj=fetchSringToObject("https://pasteme.cn/_api/backend/` + updata.clipboardID + `,nirvana?json=true");"rule://"+base64Encode(ruleObj.content);`,
                sort: status.sort
            }, {
                title: updata.name + "更新链接",
                col_type: "icon_2",
                img: "https://cdn.jsdelivr.net/gh/Lingyan000/pic@master/img/20201008221756.png",
                url: updata.updateUrl,
                sort: status.sort
            },
            {
                title: updata.name + "更新日志",
                col_type: "icon_2",
                img: "https://cdn.jsdelivr.net/gh/Lingyan000/pic@master/img/20201008221832.png",
                url: `hiker://empty@rule=js:setHomeResult({data: [{title: '<p style="text-align: center;"><strong>` + updata.name + `<font color="#f06868"> V` + updata.version + `</font> 更新日志</strong></p><br/>` + updata.updateLog + `',col_type: "rich_text"}]});`,
                sort: status.sort
            }
        ]);
    });
    if (items.length <= 0) items.push({
        title: "‘‘没有需要检查的规则’’",
        col_type: "text_center_1"
    });
    else {
        items.push({
            title: `‘‘’’<strong><font color="#ffaa64">一键导入</font><strong>`,
