for(var i=0;i<json.list.length;i++){

//把json数组里的值存到变量 j
var j = json.list[i];
var r = {};

//把json里的子值存到对应海阔所需的键里

//标题
r.title = j.title;

//图片地址
r.pic_url=j.thumb;

//描述
r.desc=j.imgsize;

//链接,也为图片地址不跳转网页直接显示大图,为视频播放地址同理会直接播放
r.url=j.img;d.push(r);
}

//把d的数据存到res对象里,并输出到首页
res.data = d;
setHomeResult(res);

